import React from 'react';
import PropTypes from 'prop-types';
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import {connect} from "react-redux";

const AntSwitch = withStyles((theme) => ({
    root: {
        width: 28,
        height: 16,
        padding: 0,
        display: 'flex',
    },
    switchBase: {
        padding: 2,
        color: theme.palette.grey[500],
        '&$checked': {
            transform: 'translateX(12px)',
            color: theme.palette.common.white,
            '& + $track': {
                opacity: 1,
                backgroundColor: theme.palette.primary.main,
                borderColor: theme.palette.primary.main,
            },
        },
    },
    thumb: {
        width: 12,
        height: 12,
        boxShadow: 'none',
    },
    track: {
        border: `1px solid ${theme.palette.grey[500]}`,
        borderRadius: 16 / 2,
        opacity: 1,
        backgroundColor: theme.palette.common.white,
    },
    checked: {},
}))(Switch);

BattingCard.propTypes = {

};

function BattingCard(props) {

    const [state, setState] = React.useState({
        checkedA: true,
        checkedB: true,
        checkedC:true
    });

    const handleChange = (event) => {
        setState({ ...state, [event.target.name]: event.target.checked });
    };

    const [scoreCardData,setScoreCardData]=React.useState();

    React.useEffect(()=>{
        // eslint-disable-next-line no-lone-blocks
        {
            props.selectedMatchDetails &&
            fetch(`https://dev132-cricket-live-scores-v1.p.rapidapi.com/scorecards.php?seriesid=${props.selectedMatchDetails.meta.series.id}&matchid=${props.selectedMatchDetails.matchDetail.matchSummary.id}`, {
                "method": "GET",
                "headers": {
                    "x-rapidapi-host": "dev132-cricket-live-scores-v1.p.rapidapi.com",
                    "x-rapidapi-key": "a2e362eba7mshf79992f1cbe7a73p1c653ejsn87bd2054f646"
                }
            }).then(res => res.json())
                .then(data => setScoreCardData(data))
        }
    },[props.selectedMatchDetails])

    console.log(scoreCardData,'scoreCardData')

    return (
        <div>
            <Typography component="div">
                {props.selectedMatchDetails && props.selectedMatchDetails.matchDetail.innings[0].id !==0  && <Grid component="label" container alignItems="center" spacing={1}>
                    <Grid item>{ props.selectedMatchDetails.matchDetail.innings[0].shortName}</Grid>
                    <Grid item>
                        <AntSwitch checked={state.checkedC} onChange={handleChange} name="checkedC"/>
                    </Grid>
                    <Grid item>{props.selectedMatchDetails.matchDetail.innings[1].shortName}</Grid>
                </Grid>
                }
                <h4>{scoreCardData.fullScorecard.innings[0].name}</h4>
                <table>
                    <tr>

                    </tr>
                </table>
            </Typography>
        </div>
    );
}

function mapStateToProps(state){
    return{
        selectedMatchDetails:state.sports.selectedMatchDetails
    }
}

export default connect(mapStateToProps) (BattingCard)
