import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import PlusOneRoundedIcon from '@material-ui/icons/PlusOneRounded';
import ToDoModal from "./ToDoModal";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

// import 'date-fns';

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
    formControl: {
        minWidth: 120,
    },
});



export default function TaskTable() {

    const [tableData,setTableData]=React.useState();

    console.log(tableData,'tableData')
    React.useEffect(()=>{
        fetch("http://localhost:5000/users",{
            method:"GET"
        }).then(res=>res.json())
            .then(data=>setTableData(data))
    },[])
    const classes = useStyles();

    const [showAddTaskModal, setShowAddTaskModal] = React.useState(false)

    const addNewTask = () => {
        setShowAddTaskModal(!showAddTaskModal)
    }

    function createData(empName, taskName, status) {
        return {empName, taskName, status};
    }

    const rows = tableData && tableData.map((row)=>{
        return createData(row.empName, row.taskName, row.status)
    })
    ;

    const onSubmit=()=>{

        const postObject= {
            userName: empName,
            taskName: taskName,
            status: status
        };

        console.log(postObject,'postObject')

        fetch('http://localhost:5000/users/add',{
            method:"Post",
            body:JSON.stringify(postObject),
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                // key: "Content-Type",
                // value: "application/json"
            }
        }).then(res=>res.json())
            .then(data=>console.log(data))
            .catch(err=>console.log(err))
    }

    const [status, setStatus] = React.useState('');
    const [open, setOpen] = React.useState(false);
    const [taskName, setTaskName] = React.useState();
    const [empName, setEmpName] = React.useState()

    const handleChange = (event) => {
        setStatus(event.target.value);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleOpen = () => {
        setOpen(true);
    };

    const handleEmployeeName = (e) => {
        setEmpName(e.target.value)
    }

    const handleTaskName=(e)=>{
        setTaskName(e.target.value)
    }

    return (
        <React.Fragment>
            {!showAddTaskModal ?
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead>
                            <TableRow style={{border: '1px solid transparent'}}>
                                <TableCell>Task</TableCell>
                                <TableCell align="right">Status</TableCell>
                                <TableCell align="right">Priority</TableCell>
                                <TableCell align="right">Progress</TableCell>
                            </TableRow>

                            <TableRow style={{width: '100%', cursor: 'pointer'}}>
                                <TableCell style={{border: '2px dashed green'}} onClick={addNewTask}>
                                    <PlusOneRoundedIcon style={{color: '#5cb61b', marginBottom: '-1.5%'}}/>
                                    <span style={{
                                        marginLeft: "2%",
                                        color: '#5cb61b',
                                        fontSize: '16px'
                                    }}>Add New Task</span>
                                </TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {rows && rows.map((row) => (
                                <TableRow key={row.empName}>
                                    <TableCell component="th" scope="row">
                                        {row.empName}
                                    </TableCell>
                                    <TableCell align="right">{row.taskName}</TableCell>
                                    <TableCell align="right">{row.status}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                :
                <React.Fragment>
                    <Grid container spacing={2}>
                        <Grid item xs={3}>

                        </Grid>

                        <Grid item xs={6}>
                            <Paper style={{width: '100%'}}>

                                <Grid container spacing={2} style={{marginTop: "2%"}}>
                                    <Grid item xs={6}>
                                        <TextField id="outlined-basic"
                                                   label="Employee Name"
                                                   onChange={handleEmployeeName}
                                                   value={empName}
                                                   variant="outlined"/>
                                        <div>
                                            <TextField id="outlined"
                                                       label="Task Name"
                                                       onChange={handleTaskName}
                                                       value={taskName}
                                                       variant="outlined"/>
                                        </div>


                                    </Grid>

                                    <Grid item xs={6}>
                                        <FormControl className={classes.formControl}>
                                            <InputLabel id="demo-controlled-open-select-label">Select
                                                Status</InputLabel>
                                            <Select
                                                labelId="demo-controlled-open-select-label"
                                                id="demo-controlled-open-select"
                                                open={open}
                                                onClose={handleClose}
                                                onOpen={handleOpen}
                                                value={status}
                                                onChange={handleChange}
                                            >
                                                <MenuItem value="">
                                                    <em>None</em>
                                                </MenuItem>
                                                <MenuItem value={'Started'}>Started</MenuItem>
                                                <MenuItem value={'Yet to pick'}>Yet to pick</MenuItem>
                                                <MenuItem value={'Completed'}>Completed</MenuItem>
                                            </Select>
                                        </FormControl>

                                        <div>
                                           {/*<MaterialUIPickers/>*/}
                                        </div>
                                    </Grid>
                                </Grid>

                                <button onClick={addNewTask}>
                                    Cancel
                                </button>

                                <button onClick={onSubmit}>
                                    Submit
                                </button>
                            </Paper>
                        </Grid>

                        <Grid item xs={3}>

                        </Grid>
                    </Grid>


                </React.Fragment>
            }
        </React.Fragment>

    );
}
