import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import ScoreCard from "./ScoreCard";
import Grid from '@material-ui/core/Grid';

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`scrollable-auto-tabpanel-${index}`}
            aria-labelledby={`scrollable-auto-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box p={3}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `scrollable-auto-tab-${index}`,
        'aria-controls': `scrollable-auto-tabpanel-${index}`,
    };
}

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        width: '100%',
        backgroundColor: theme.palette.background.paper,
    },
}));

export default function MatchesCard(props) {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    console.log(props.matchesData,'matchesData')

    return (
        <div className={classes.root}>
            <AppBar position="static" color="default">
                <Tabs
                    value={value}
                    onChange={handleChange}
                    indicatorColor="primary"
                    textColor="primary"
                    variant="scrollable"
                    scrollButtons="auto"
                    aria-label="scrollable auto tabs example"
                >
                {props.matchesData && props.matchesData.map((match,index)=>{
                    return <Tab label={match.series.name + '-' + match.name} {...a11yProps(index)} />
                })}
                </Tabs>

            </AppBar>

            {props.matchesData && props.matchesData.map((match,index)=>{
                return <TabPanel value={value} index={index}>
                    <Grid container spacing={1}>
                       <Grid item xs={4}>
                           <ScoreCard matchData={match}/>
                       </Grid>

                        <Grid item xs={4}>
                            Batting Stats

                            <a>{props.match.currentBatters[0].name}</a>
                        </Grid>

                        <Grid item xs={4}>
                            Bowling Stats
                        </Grid>
                    </Grid>


                </TabPanel>
                }
            )}
        </div>
    );
}
