import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import {red} from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import {API_URL} from "../../../reduxModules/constants";
import {connect} from "react-redux";
import SNLandingPage from "./SNLandingPage";
import {Grid} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
    root: {
        padding: "20px",
        marginTop: "4%",
        width: "100%",
        MuiCardHeader: {
            title: {
                float: "left"
            }
        }
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
}));

function PostCard(props) {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const [postsData, setPostsData] = React.useState([]);
    const [showUnlike, setShowUnlike] = React.useState(false);
    const [updatePostData,setUpdatePostData]=React.useState(false);

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    React.useEffect(() => {
        fetch(API_URL + "/post/allposts", {
            method: "GET",
            headers: {
                "Authorization": "Bearer " + props.jwtToken.userDetails.accessToken
            }
        })
            .then(res => res.json())
            .then(data => setPostsData(data.posts))
    }, [updatePostData])

    const handleLikeClick = (id) => {
        fetch(API_URL + "/post/like", {
            method: "PUT",
            body: JSON.stringify({
                postId: id
            }),
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Authorization": "Bearer " + props.jwtToken.userDetails.accessToken
            }
        }).then(res => res.json())
            .then(data => console.log(data))
            .then(()=>setUpdatePostData(!updatePostData))
            .finally(() => setShowUnlike(true))
    }

    const handleUnLikeClick = (id) => {
        fetch(API_URL + "/post/unlike", {
            method: "PUT",
            body: JSON.stringify({
                postId: id
            }),
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Authorization": "Bearer " + props.jwtToken.userDetails.accessToken
            }
        }).then(res => res.json())
            .then(data => console.log(data))
            .then(()=>setUpdatePostData(!updatePostData))
            .finally(() => setShowUnlike(false))
    }

    console.log(postsData, 'postsData')

    return (
        <div>
            <SNLandingPage/>
            <Grid container spacing={3}>
                <Grid item xs={3}>

                </Grid>
                <Grid item xs={6}>
                    {postsData.map((post, index) => {
                        return (
                            <Card className={classes.root} key={index}>
                                <Grid container spacing={3} style={{padding: "20px"}}>
                                    <Grid item xs={1}>
                                        <Avatar/>
                                    </Grid>
                                    <Grid item xs={3} style={{textAlign: "left", marginTop: "10px"}}>
                                        <span className="userName">{post.postedBy.name}</span>
                                    </Grid>
                                </Grid>
                                <CardMedia
                                    className={classes.media}
                                    image={post.photo}
                                    title={post.title}
                                />
                                <CardContent>
                                    <Typography variant="body2" color="textSecondary" component="p">
                                        {post.description}
                                    </Typography>
                                </CardContent>
                                <CardActions disableSpacing>
                                    <IconButton aria-label="add to favorites">
                                        {showUnlike ?
                                            <FavoriteIcon style={{fill: "#dd0c0c"}} onClick={()=>handleUnLikeClick(post._id)}/> :
                                            <FavoriteIcon onClick={()=>handleLikeClick(post._id)}/>}
                                    </IconButton>
                                    <IconButton aria-label="share">
                                        <ShareIcon/>
                                    </IconButton>
                                </CardActions>
                            </Card>
                        )
                    })}
                </Grid>
                <Grid item xs={3}>
                </Grid>
            </Grid>


        </div>

    );
}

function mapStateToProps(state) {
    return {
        jwtToken: state.accessToken
    }
}

export default connect(mapStateToProps)(PostCard)
