import React from 'react';
import PropTypes from 'prop-types';
import {Grid} from "@material-ui/core";
import "./snstyle.css";
import SNLandingPage from "./SNLandingPage";
import {API_URL} from "../../../reduxModules/constants";
import {connect} from "react-redux";
import Button from "@material-ui/core/Button";
import makeStyles from "@material-ui/core/styles/makeStyles";
import EditIcon from '@material-ui/icons/Edit';

Profile.propTypes = {};

const useStyles = makeStyles((theme) => ({
    button: {
        margin: theme.spacing(1),
    },
}));

function Profile(props) {
    const classes = useStyles();
    const [myPostsData, setMyPostsData] = React.useState();

    React.useEffect(() => {
        fetch(API_URL + "/post/myPosts", {
            method: "GET",
            headers: {
                "Authorization": "Bearer " + props.jwtToken.userDetails.accessToken
            }
        }).then(res => res.json())
            .then(data => setMyPostsData(data.posts))
    }, [])

    console.log(myPostsData, 'myPostsData')
    return (
        <div>
            <SNLandingPage/>
            <Grid container spacing={3}>
                <Grid item xs={2}>

                </Grid>

                <Grid item xs={8}>
                    <div className="profileHeader">
                        <div className="thumbnail">
                            <div className="thumbnailDiv">
                                <img
                                    src="https://images.unsplash.com/photo-1599687350916-a8be7f9b68f1?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=800&q=60"
                                    className="thumbnailDiv"/>
                            </div>

                            <div className="profileStats">
                                <h3 className="profileName">{myPostsData && myPostsData[0].postedBy.name}</h3>
                                <Button
                                    style={{marginTop:"10%",float: "right",width:"250px",height:"50px",marginLeft:"2%"}}
                                    variant="contained"
                                    // color="secondary"
                                    className={classes.button}
                                    startIcon={<EditIcon/>}
                                >
                                    Edit Profile
                                </Button>
                            </div>

                        </div>

                    </div>
                </Grid>

                <Grid item xs={2}>

                </Grid>

            </Grid>
        </div>
    );
}

function mapStateToProps(state) {
    return {
        jwtToken: state.accessToken
    }
}

export default connect(mapStateToProps)(Profile)
