import React from 'react';
import PropTypes from 'prop-types';
import TextField from "@material-ui/core/TextField";
import Typography from "@material-ui/core/Typography";
import FacebookLogin from "react-facebook-login/dist/facebook-login-render-props";
import Button from "@material-ui/core/Button";
import {FaFacebook} from "react-icons/fa";
import GoogleLogin from "react-google-login";
import {FcGoogle} from "react-icons/fc";
import Paper from "@material-ui/core/Paper";
import {createMuiTheme, makeStyles, withStyles} from "@material-ui/core/styles";
import history from "../../utils/history";
import {green} from "@material-ui/core/colors";
import {showSignUpOrSignIn, StoreLoggedInUserDetails} from "../../reduxModules/actions";
import {connect} from "react-redux";

const BootstrapButton = withStyles({
    root: {
        boxShadow: 'none',
        width: '100%',
        textTransform: 'none',
        fontSize: 16,
        padding: '6px 12px',
        border: '1px solid',
        lineHeight: 1.5,
        backgroundColor: '#0063cc',
        background: 'linear-gradient(to right, #6699ff 0%, #ff66cc 100%)',
        borderColor: '#0063cc',
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
        '&:hover': {
            backgroundColor: '#0069d9',
            borderColor: '#0062cc',
            boxShadow: 'none',
        },
        '&:active': {
            boxShadow: 'none',
            backgroundColor: '#0062cc',
            borderColor: '#005cbf',
        },
        '&:focus': {
            boxShadow: '0 0 0 0.2rem rgba(0,123,255,.5)',
        },
    },
})(Button);
Login.propTypes = {};


const useStyles = makeStyles((theme) => ({
    margin: {
        marginTop: '10%',
        margin: theme.spacing(1),
    },
}));

const theme = createMuiTheme({
    palette: {
        primary: green,
    },
});

function Login(props) {


    const [userDetails, setUserDetails] = React.useState();

    const responseFacebook = (response) => {
        props.StoreLoggedInUserDetails(response);
        setUserDetails(response)
        console.log(response);
    }

    const responseGoogle = (response) => {
        props.StoreLoggedInUserDetails(response);
        setUserDetails(response)
        console.log(response);
    }

    const routeToHome = () => {
        props.StoreLoggedInUserDetails('looged in');
        fetch("http://localhost:5000/auth/signin",{
            method: "POST",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                // key: "Content-Type",
                // value: "application/json"
            }
        }).then(res => res.json())
            .then(data=>console.log(data))
        history.push('/')
    }

    const routeToSignUp=()=>{
        props.showSignUpOrSignIn(false);

    }

    const classes = useStyles();

    return (
        <div>
            <div style={{padding: "20px"}}>
                <h3>Login</h3>
                <div style={{minWidth: '400px'}}>
                    <TextField style={{width: '100%'}} id="standard-basic" label="Username"/>
                </div>

                <div style={{minWidth: '400px'}}>
                    <TextField style={{width: '100%'}} id="standard-basic" label="Password"
                               type='password'/>
                </div>
                <BootstrapButton variant="contained" color="primary"
                                 onClick={routeToHome}
                                 className={classes.margin}>
                    Login
                </BootstrapButton>

                <Typography variant="caption" display="block" gutterBottom>
                    No Account !!! Want to <span style={{color:"#4dda30",fontSize:"18px",cursor:"pointer"}}
                                                 onClick={routeToSignUp}
                > Sign Up here !!!</span>

                </Typography>

                <div style={{marginTop: '4%'}}>
                                    <span style={{color: 'gray', fontSize: '12px'}}>
                                    Or Sign up using
                                </span>

                    <div style={{marginLeft: "2%"}}>
                        <div style={{display: 'flex', width: '100%'}}>
                            <div style={{marginTop: '10px', width: '50%'}}>
                                <FacebookLogin
                                    style={{fontSize: '14px'}}
                                    appId="2671267476235064" //APP ID NOT CREATED YET
                                    fields="name,email,picture"
                                    render={renderProps => (
                                        <Button style={{
                                            width: '50px',
                                            backgroundColor: '#4267B2',
                                            color: 'white',
                                            height: '40px',
                                            fontSize: "14px"
                                        }} onClick={renderProps.onClick}><FaFacebook
                                            size={'2em'}/></Button>
                                    )}
                                    callback={responseFacebook}
                                />
                            </div>

                            <div style={{width: '50%'}}>
                                <Button>
                                    <GoogleLogin
                                        clientId="678811344605-9ldaqegvtq8khga70lccdsj32ok1oiis.apps.googleusercontent.com" //CLIENTID NOT CREATED YET
                                        onSuccess={responseGoogle}
                                        render={renderProps => (
                                            <Button style={{
                                                width: '50px',
                                                backgroundColor: '#fff',
                                                border: '1px solid gray',
                                                color: 'white',
                                                height: '40px',
                                                fontSize: "14px"
                                            }} onClick={renderProps.onClick}><FcGoogle
                                                size={'2em'}/></Button>
                                        )}
                                        onFailure={responseGoogle}
                                    />
                                </Button>
                            </div>
                        </div>
                    </div>
                </div>

            </div>
        </div>
    );
}

const mapDispatchToProps = (dispatch) => {
    return {
        dispatch,
        StoreLoggedInUserDetails: (payload) => dispatch(StoreLoggedInUserDetails(payload)),
        showSignUpOrSignIn: (payload) => dispatch(showSignUpOrSignIn(payload))
    }
}

export default connect(null, mapDispatchToProps) (Login);
