import React, {Component} from 'react';
// import './App.css';

import FacebookLogin from 'react-facebook-login/dist/facebook-login-render-props'
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import GoogleLogin from 'react-google-login';
import {StoreLoggedInUserDetails} from "../../reduxModules/actions";
import {connect} from "react-redux";
import Background from '../../images/snowFall.jpeg';
import AppBar from '../home/AppBar';
import AppBackground from '../../images/AppBackground.jpeg';
import {FaFacebook} from 'react-icons/fa';
import {FcGoogle} from 'react-icons/fc';
import { createMuiTheme, withStyles, makeStyles, ThemeProvider } from '@material-ui/core/styles';
import { green, purple } from '@material-ui/core/colors';

const backgroundImage = {
    width: "100%",
    display: 'flex',
    minHeight: 'calc(100vh)',
    // height: "100%",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    overflow: 'hidden',
    /*// Calculated from the aspect ration of the content (in case of 16:9 it is 9/16= 0.5625)*/
// padding-top: 46.25%;
    position: "relative",
    backgroundPosition: 'center',
    backgroundImage: `url(${Background})`
}

const AppBackgroundImage = {
    width: "100%",
    display: 'flex',
    minHeight: 'calc(100vh)',
    // height: "100%",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    overflow: 'hidden',
    /*// Calculated from the aspect ration of the content (in case of 16:9 it is 9/16= 0.5625)*/
// padding-top: 46.25%;
    position: "relative",
    backgroundPosition: 'center',
    backgroundImage: `url(${AppBackground})`
}

const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(1),
    },
}));

const theme = createMuiTheme({
    palette: {
        primary: green,
    },
});

class Authentication extends Component {

    constructor(props) {
        super(props);
        this.state = {
            userDetails: null
        }
    }

    render() {

        const responseFacebook = (response) => {
            this.props.StoreLoggedInUserDetails(response);
            this.setState({
                userDetails: response
            });
            console.log(response);
        }

        const responseGoogle = (response) => {
            this.props.StoreLoggedInUserDetails(response);
            this.setState({
                userDetails: response
            });
            console.log(response);
        }

        const classes = useStyles();

        return (
            <div className="App">
                {!this.state.userDetails ?
                    <div style={backgroundImage}>
                        <Paper style={{margin: 'auto'}}>

                            <div style={{padding: "20px"}}>
                                <h3>Login</h3>
                                <div style={{minWidth:'400px'}}>
                                    <TextField style={{width:'100%'}} id="standard-basic" label="Username"/>
                                </div>

                                <div style={{minWidth:'400px'}}>
                                    <TextField style={{width:'100%'}} id="standard-basic" label="Password" type='password'/>
                                </div>

                                <Button variant="contained" color="primary" className={classes.margin}>
                                    Login
                                </Button>

                                <div style={{marginTop:'4%'}}>
                                    <span style={{color: 'gray', fontSize: '12px'}}>
                                    Or Sign up using
                                </span>

                                    <div style={{marginLeft: "2%"}}>
                                        <div style={{display: 'flex',width:'100%'}}>
                                            <div style={{marginTop: '10px',width:'50%'}}>
                                                <FacebookLogin
                                                    style={{fontSize: '14px'}}
                                                    appId="2671267476235064" //APP ID NOT CREATED YET
                                                    fields="name,email,picture"
                                                    render={renderProps => (
                                                        <Button style={{
                                                            width: '50px',
                                                            backgroundColor: '#4267B2',
                                                            color: 'white',
                                                            height: '40px',
                                                            fontSize: "14px"
                                                        }} onClick={renderProps.onClick}><FaFacebook
                                                            size={'2em'}/></Button>
                                                    )}
                                                    callback={responseFacebook}
                                                />
                                            </div>

                                            <div style={{width: '50%'}}>
                                                <Button>
                                                    <GoogleLogin
                                                        clientId="678811344605-9ldaqegvtq8khga70lccdsj32ok1oiis.apps.googleusercontent.com" //CLIENTID NOT CREATED YET
                                                        onSuccess={responseGoogle}
                                                        render={renderProps => (
                                                            <Button style={{
                                                                width: '50px',
                                                                backgroundColor: '#fff',
                                                                border: '1px solid gray',
                                                                color: 'white',
                                                                height: '40px',
                                                                fontSize: "14px"
                                                            }} onClick={renderProps.onClick}><FcGoogle
                                                                size={'2em'}/></Button>
                                                        )}
                                                        onFailure={responseGoogle}
                                                    />
                                                </Button>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                            </div>
                        </Paper>
                    </div>

                    :
                    <div style={AppBackgroundImage}>
                        <AppBar/>
                    </div>
                }


            </div>
        );
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        dispatch,
        StoreLoggedInUserDetails: (payload) => dispatch(StoreLoggedInUserDetails(payload))
    }
}

// function mapStateToProps(state) {
//     return{
//         userDetails:state.loggedInUserDetails
//     }
//
// }

export default connect(null, mapDispatchToProps)(Authentication);
