import React, {Component} from 'react';
// import './App.css';
import { Router, Switch} from "react-router-dom";
import FacebookLogin from 'react-facebook-login/dist/facebook-login-render-props'
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import GoogleLogin from 'react-google-login';
import {StoreLoggedInUserDetails} from "../../reduxModules/actions";
import {connect} from "react-redux";
import Background from '../../images/snowFall.jpeg';
import AppBar from '../home/AppBar';
import AppBackground from '../../images/AppBackground.jpg';
import {FaFacebook} from 'react-icons/fa';
import {FcGoogle} from 'react-icons/fc';
import {createMuiTheme, withStyles, makeStyles, ThemeProvider} from '@material-ui/core/styles';
import {green, purple} from '@material-ui/core/colors';
import PrivateRoute from "../../PrivateRouter";
import history from "../../utils/history";
import LandingPage from '../home/LandingPage';
import PersonalInfoLandingPage from "../personalInfo/PersonalInfoLandingPage";
import RoutingPage from "../home/RoutingPage";
import Typography from "@material-ui/core/Typography";

const BootstrapButton = withStyles({
    root: {
        boxShadow: 'none',
        width: '100%',
        textTransform: 'none',
        fontSize: 16,
        padding: '6px 12px',
        border: '1px solid',
        lineHeight: 1.5,
        backgroundColor: '#0063cc',
        background: 'linear-gradient(to right, #6699ff 0%, #ff66cc 100%)',
        borderColor: '#0063cc',
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
        '&:hover': {
            backgroundColor: '#0069d9',
            borderColor: '#0062cc',
            boxShadow: 'none',
        },
        '&:active': {
            boxShadow: 'none',
            backgroundColor: '#0062cc',
            borderColor: '#005cbf',
        },
        '&:focus': {
            boxShadow: '0 0 0 0.2rem rgba(0,123,255,.5)',
        },
    },
})(Button);

const backgroundImage = {
    width: "100%",
    display: 'flex',
    minHeight: 'calc(100vh)',
    // height: "100%",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    overflow: 'hidden',
    /*// Calculated from the aspect ration of the content (in case of 16:9 it is 9/16= 0.5625)*/
// padding-top: 46.25%;
    position: "relative",
    backgroundPosition: 'center',
    backgroundImage: `url(${Background})`
}



const useStyles = makeStyles((theme) => ({
    margin: {
        marginTop: '10%',
        margin: theme.spacing(1),
    },
}));

const theme = createMuiTheme({
    palette: {
        primary: green,
    },
});

function Authentication(props) {

    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         userDetails: null
    //     }
    // }

    const [userDetails, setUserDetails] = React.useState();

    const responseFacebook = (response) => {
        props.StoreLoggedInUserDetails(response);
        setUserDetails(response)
        console.log(response);
    }

    const responseGoogle = (response) => {
        props.StoreLoggedInUserDetails(response);
        setUserDetails(response)
        console.log(response);
    }

    const routeToHome=()=>{
        props.StoreLoggedInUserDetails('looged in');
        history.push('/')
    }

    const classes = useStyles();

    return (
        <div className="App">
            {
                !props.userDetails ?
                <div style={backgroundImage}>
                    <Paper style={{margin: 'auto'}}>


                    </Paper>
                </div>

                :
                <RoutingPage/>

            }


        </div>
    );

}

const mapDispatchToProps = (dispatch) => {
    return {
        dispatch,
        StoreLoggedInUserDetails: (payload) => dispatch(StoreLoggedInUserDetails(payload))
    }
}

function mapStateToProps(state) {
    return{
        userDetails:state.loggedInUserDetails
    }

}

export default connect(mapStateToProps, mapDispatchToProps)(Authentication);
