import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import PlusOneRoundedIcon from '@material-ui/icons/PlusOneRounded';
import ToDoModal from "./ToDoModal";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid'
const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

function createData(name, calories, fat, carbs, protein) {
    return {name, calories, fat, carbs, protein};
}

const rows = [
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    createData('Eclair', 262, 16.0, 24, 6.0),
    createData('Cupcake', 305, 3.7, 67, 4.3),
    createData('Gingerbread', 356, 16.0, 49, 3.9),
];

export default function TaskTable() {
    const classes = useStyles();

    const [showAddTaskModal, setShowAddTaskModal] = React.useState(false)

    const addNewTask = () => {
        setShowAddTaskModal(!showAddTaskModal)
    }

    const [age, setAge] = React.useState('');
    const [open, setOpen] = React.useState(false);

    const handleChange = (event) => {
        setAge(event.target.value);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleOpen = () => {
        setOpen(true);
    };

    return (
        <React.Fragment>
            {!showAddTaskModal ?
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead>
                            <TableRow style={{border: '1px solid transparent'}}>
                                <TableCell>Task</TableCell>
                                <TableCell align="right">End Date</TableCell>
                                <TableCell align="right">Status</TableCell>
                                <TableCell align="right">Priority</TableCell>
                                <TableCell align="right">Progress</TableCell>
                            </TableRow>

                            <TableRow style={{width: '100%', cursor: 'pointer'}}>
                                <TableCell style={{border: '2px dashed green'}} onClick={addNewTask}>
                                    <PlusOneRoundedIcon style={{color: '#5cb61b', marginBottom: '-1.5%'}}/>
                                    <span style={{
                                        marginLeft: "2%",
                                        color: '#5cb61b',
                                        fontSize: '16px'
                                    }}>Add New Task</span>
                                </TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {rows.map((row) => (
                                <TableRow key={row.name}>
                                    <TableCell component="th" scope="row">
                                        {row.name}
                                    </TableCell>
                                    <TableCell align="right">{row.calories}</TableCell>
                                    <TableCell align="right">{row.fat}</TableCell>
                                    <TableCell align="right">{row.carbs}</TableCell>
                                    <TableCell align="right">{row.protein}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                :
                <React.Fragment>
                <Grid container spacing={2}>
                    <Grid item xs={3}>

                    </Grid>

                    <Grid item xs={6}>
                        <Paper style={{width:'100%'}}>
                            <TextField id="outlined-basic" label="Task Name" variant="outlined" />

                            <FormControl className={classes.formControl}>
                                <InputLabel id="demo-controlled-open-select-label">Age</InputLabel>
                                <Select
                                    labelId="demo-controlled-open-select-label"
                                    id="demo-controlled-open-select"
                                    open={open}
                                    onClose={handleClose}
                                    onOpen={handleOpen}
                                    value={age}
                                    onChange={handleChange}
                                >
                                    <MenuItem value="">
                                        <em>None</em>
                                    </MenuItem>
                                    <MenuItem value={10}>Started</MenuItem>
                                    <MenuItem value={20}>Twenty</MenuItem>
                                    <MenuItem value={30}>Thirty</MenuItem>
                                </Select>
                            </FormControl>

                            <button onClick={addNewTask}>
                                Cancel
                            </button>
                        </Paper>
                    </Grid>

                    <Grid item xs={3}>

                    </Grid>
                </Grid>


                </React.Fragment>
            }
        </React.Fragment>

    );
}
