import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import {red} from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: 345,
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
}));

export default function ScoreCard(props) {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const [selectedMatchData, setSelectedMatchData] = React.useState();

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    React.useEffect(() => {
        fetch(`https://dev132-cricket-live-scores-v1.p.rapidapi.com/matchdetail.php?seriesid=${props.matchData.series.id}&matchid=${props.matchData.id}`, {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "dev132-cricket-live-scores-v1.p.rapidapi.com",
                "x-rapidapi-key": "a2e362eba7mshf79992f1cbe7a73p1c653ejsn87bd2054f646"
            }
        })
            .then(res => res.json())
            .then(data => setSelectedMatchData(data))
    }, [])

    console.log(selectedMatchData, 'selectedMatchData')

    const BattersData = selectedMatchData && selectedMatchData.matchDetail.currentBatters;

    return (
        <Card className={classes.root}>
            <CardHeader
                avatar={
                    <Avatar aria-label="recipe" className={classes.avatar}>
                        R
                    </Avatar>
                }
                action={
                    <IconButton aria-label="settings">
                        <MoreVertIcon/>
                    </IconButton>
                }
                title={props.matchData.series.name}
                subheader={props.matchData.startDateTime}
            />

            {selectedMatchData && <CardContent>
                <Typography variant="body2" color="textSecondary" component="p">
                    {selectedMatchData.matchDetail.matchSummary.matchSummaryText}
                </Typography>
                <img src={selectedMatchData.matchDetail.teamBatting.logoUrl} style={{height: "100px", width: "150px"}}/>
                <h3>{`${selectedMatchData.matchDetail.teamBatting.shortName} :  ${selectedMatchData.matchDetail.teamBatting.score}`}</h3>

                <h5>{selectedMatchData.matchDetail.matchSummaryText}</h5>
                {/*Batting:- <span>{BattersData && `${BattersData[0].name} -  ${BattersData[0].runs}  (${BattersData[0].ballsFaced})`}</span>*/}
                {/*<span style={{*/}
                {/*    display: 'flex',*/}
                {/*    marginTop: "3%"*/}
                {/*}}>{BattersData && `${BattersData[1].name} -  ${BattersData[1].runs}  (${BattersData[1].ballsFaced})`}</span>*/}
            </CardContent>
            }
            <CardActions disableSpacing>
                <IconButton aria-label="add to favorites">
                    <FavoriteIcon/>
                </IconButton>
                <IconButton aria-label="share">
                    <ShareIcon/>
                </IconButton>
                <IconButton
                    className={clsx(classes.expand, {
                        [classes.expandOpen]: expanded,
                    })}
                    onClick={handleExpandClick}
                    aria-expanded={expanded}
                    aria-label="show more"
                >
                    <ExpandMoreIcon/>
                </IconButton>
            </CardActions>
        </Card>
    );
}
