// import 'date-fns';

const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
    formControl: {
        minWidth: 120,
    },
});

export default function TaskTable() {

    const [tableData, setTableData] = React.useState();

    console.log(tableData, 'tableData')

    const [refreshData, setrefreshData] = React.useState(false);
    React.useEffect(() => {
        fetch("http://localhost:5000/users", {
            method: "GET"
        }).then(res => res.json())
            .then(data => setTableData(data))
    }, [refreshData])


    const [showAddTaskModal, setShowAddTaskModal] = React.useState(false)

    const addNewTask = () => {
        history.push('/addTodo')
    }

    function createData(userName, taskName, status, _id) {
        return {userName, taskName, status, _id};
    }

    const rows = tableData && tableData.map((row) => {
        return createData(row.userName, row.taskName, row.status, row._id)
    });

    const deleteTodoItem = (id) => {
        fetch('http://localhost:5000/users/delete/' + id, {
            method: "Delete"
        }).then(() => setrefreshData(!refreshData))
    }

    const editTodoItem=(id)=>{
                 // fetch("https://localhost:5000/user/edit"+id,{
                 //     method:"PUT",
                 //     body
                 // })
    }

    const classes = useStyles();

    return (
        <React.Fragment>
            <TableContainer component={Paper}>
                <Table className={classes.table} aria-label="simple table">
                    <TableHead>
                        <TableRow style={{border: '1px solid transparent'}}>
                            <TableCell>Task</TableCell>
                            <TableCell align="right">Status</TableCell>
                            <TableCell align="right">Priority</TableCell>
                            <TableCell align="right">Action</TableCell>
                        </TableRow>

                        <TableRow style={{width: '100%', cursor: 'pointer'}}>
                            <TableCell style={{border: '2px dashed green'}} onClick={addNewTask}>
                                <PlusOneRoundedIcon style={{color: '#5cb61b', marginBottom: '-1.5%'}}/>
                                <span style={{
                                    marginLeft: "2%",
                                    color: '#5cb61b',
                                    fontSize: '16px'
                                }}>Add New Task</span>
                            </TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows && rows.map((row) => (
                            <TableRow key={row.userName}>
                                <TableCell component="th" scope="row">
                                    {row.userName}
                                </TableCell>
                                <TableCell align="right">{row.taskName}</TableCell>
                                <TableCell align="right">{row.status}</TableCell>
                                <TableCell align="right">
                                    <DeleteOutlineIcon onClick={() => deleteTodoItem(row._id)}/>
                                        {/*//     onClick={() => editTodoItem(row._id)}>*/}
                                        {/*// Edit*/}
                                        {/*// <button*/}
                                        {/*        onClick={() => deleteTodoItem(row._id)}>*/}
                                        {/*</button>*/}

                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </React.Fragment>

    );
}
